import React from 'react'
import ReactDOM from 'react-dom';

class App extends React.Component{

	constructor(props){
		super(props);

		//only time we do direct assisnment to this.state
		this.state = { lat: null, erromsg: ''};

		window.navigator.geolocation.getCurrentPosition(
		(position)=> {
			//setstate!!!!!!!! format!!!!
			this.setState({lat: position.coords.latitude});


		},
		(err) => {

			this.setState({erromsg: err.message});
		}

		);
	}

	componentDidMount(){
		console.log('Mounted on screen!');
	}
	componentDidUpdate(){

		console.log('Component rerenderd');
	}

	render(){


		if (this.state.erromsg && !this.state.lat){
			return <div> Error: {this.state.erromsg}</div>
		}

		if (!this.state.erromsg && this.state.lat){
			return <div> Lat: {this.state.lat}</div>
		}
		else {
			return <div>Loading!</div>
		}


	}
}



ReactDOM.render(
	<App />,
	document.querySelector('#root'));
